INSERT INTO `foods` (`id`, `tên`, `mô tả`, `giá`, `loại`, `hình ảnh`) VALUES
(20, 'Súp Nghêu Hải Sản Nguyên Chất', 'Súp được chế biến từ hỗn hợp các loại hải sản tươi ngon như nghêu, tôm, và mực, nấu cùng nước dùng rau củ tự nhiên, mang lại hương vị đậm đà và thanh mát.', 100000.00, '0', 'data:image/jpeg;base64,'),
(21, 'Salad Trộn Lá Xanh với Hạt Chia và Nước Sốt Vani', 'Sự kết hợp của các loại rau xanh tươi mát, hạt chia giòn và nước sốt vani thơm lừng, tạo nên món salad nhẹ nhàng nhưng đầy dinh dưỡng.', 200000.00, 'Khai Vị', 'https://quangon.vn/resources/2021/10/30/cach-lam-salad-le-rau-xanh-7.jpg'),
(22, 'Tartar Cá Hồi với Bơ và Trứng Cá Ngọc', 'Cá hồi tươi được cắt nhỏ và trộn cùng bơ béo ngậy, trang trí bằng trứng cá ngọc, tạo nên sự hòa quyện hoàn hảo giữa vị mặn và béo.', 150000.00, 'Khai Vị', 'https://assets.tmecosys.com/image/upload/t_web767x639/img/recipe/ras/Assets/52787766-df48-4264-bce8-b23ea08a0cd9/Derivates/1db6b5d3-fe1f-42ae-91b1-6521fdf0cb2e.jpg'),
(23, 'File Thịt Bò Wellington', 'Thịt bò tươi được bọc trong lớp pate gan và nấm, sau đó nướng chín tới, tạo nên lớp vỏ ngoài giòn rụm và thịt bên trong mềm mại.', 170000.00, 'Món Chính', 'https://file.hstatic.net/1000282430/article/b__cu_n_d_t_l__wellington_dae5b69b93874b41beb59d39d6f560dd.jpg'),
(24, 'Cá Hồng Nướng Sốt Miso và Mật Ong', 'Cá hồng tươi được ướp với sốt miso và mật ong, nướng đến khi cá chín mềm và sốt caramel hóa, mang lại hương vị ngọt ngào và đậm đà.', 120000.00, 'Món Chính', 'https://reviewamthuc.net/wp-content/uploads/2024/05/ca-hoi-nuong-mat-ong-4.jpg'),
(25, 'Risotto Nấm Rừng và Parmezan', 'Cơm risotto thơm ngon được nấu cùng các loại nấm rừng tươi, thêm parmezan để tăng hương vị béo ngậy và mịn màng.', 200000.00, 'Món Chính', 'https://cdn2.fptshop.com.vn/unsafe/Uploads/images/tin-tuc/184786/Originals/risotto-12.jpg'),
(26, 'Tôm Hùm Áp Chảo với Nước Sốt Bơ Tỏi', 'Tôm hùm tươi được áp chảo nhanh với bơ và tỏi, giữ được độ tươi ngon và vị ngọt tự nhiên của tôm.', 550000.00, 'Món Chính', 'https://cdn.tgdd.vn/2020/07/CookProduct/30-1200x676-3.jpg'),
(27, 'Gà File với Nước Sốt Rượu Vang và Hoa Cúc', 'Thịt gà được nấu chín mềm với nước sốt rượu vang đỏ và hoa cúc, tạo nên hương vị thanh lịch và tinh tế.', 130000.00, 'Món Chính', 'https://qkawine.com/wp-content/uploads/2023/11/cong-thuc-ga-nau-ruou-vang-do.jpg'),
(28, 'Chocolate Lava Cake với Kem Vanila', 'Bánh socola nóng giòn bên ngoài và mềm mịn bên trong, phục vụ kèm theo kem vani lạnh mát, tạo nên sự kết hợp hoàn hảo giữa ngọt và mát.', 50000.00, 'Tráng Miệng', 'https://i.ytimg.com/vi/MHyqpptxzXI/hq720.jpg?sqp=-oaymwEhCK4FEIIDSFryq4qpAxMIARUAAAAAGAElAADIQj0AgKJD&rs=AOn4CLDlVQ7HuEnn0ABVbr8q4DbQHuL4dA'),
(29, 'Panna Cotta Dâu Tây và Lá Bạc Hà', 'Món tráng miệng nhẹ nhàng với panna cotta kem mịn được trang trí bằng dâu tây tươi và lá bạc hà thơm ngon.', 50000.00, 'Tráng Miệng', 'https://png.pngtree.com/thumb_back/fw800/background/20220208/pngtree-panna-cotta-strawberry-with-mint-on-board-delicious-pink-vegetarian-photo-image_35295070.jpg'),
(30, 'Tiramisu Truyền Thống Ý', 'Món bánh tiramisu mềm mại, ngọt ngào với lớp kem mascarpone và hương cà phê đậm đà, mang lại cảm giác dễ chịu và thư giãn.', 50000.00, 'Tráng Miệng', 'https://file.hstatic.net/200000079049/file/cong-thuc-lam-tiramisu_e9b100c536c5412f8c4e73257d99e38c_grande.jpg'),
(31, 'Sorbet Chanh Muối với Trà Xanh Nhật Bản', 'Sorbet chanh muối tươi mát kết hợp với hương vị trà xanh Nhật Bản, tạo nên sự cân bằng giữa ngọt, chua và mặn.', 50000.00, 'Tráng Miệng', 'https://www.lorca.vn/wp-content/uploads/2023/06/tp1-11.jpg'),
